def arrayStringsAreEqual (list1,list2):
    str1=str2=""
    for item in list1:
        str1+=item;
    for item in list2:
        str2+=item;
    return str1==str2


# shortcut 
#   return "".join(word1)=="".join(word2)
arrayStringsAreEqual(["ab", "c"],["a", "bcd"])

