def findWordsContaining(words,x):
    i_list=[]
    # i=0
    # for item in words:
    #     if x in item:
    #         i_list.append(i)
    #     i+=1
    # alternative
    for index in range(len(words)):
        if x in words[index] :
            i_list.append(index)
    return i_list


